INSERT INTO public.filmes (id, tmdb_id, titulo, titulo_original, sinopse, data_lancamento, genero, poster_path, lingua, duracao, nota_media, elenco_principal, cidade_referencia, latitude, longitude, data_criacao, data_atualizacao) VALUES (21, 126963, 'Dragon Ball Z: Battle of Gods', 'ドラゴンボールZ 神と神', 'The events of Battle of Gods take place some years after the battle with Majin Buu, which determined the fate of the entire universe. After awakening from a long slumber, Beerus, the God of Destruction is visited by Whis, his attendant and learns that the galactic overlord Frieza has been defeated by a Super Saiyan from the North Quadrant of the universe named Goku, who is also a former student of the North Kai. Ecstatic over the new challenge, Goku ignores King Kai''s advice and battles Beerus, but he is easily overwhelmed and defeated. Beerus leaves, but his eerie remark of "Is there nobody on Earth more worthy to destroy?" lingers on. Now it is up to the heroes to stop the God of Destruction before all is lost.', '2013-03-30', 'Animation, Action, Science Fiction', '/nxZEdYcHMuD8SSuwusDnK9CD2H1.jpg', 'ja', 85, 6.8, 'Masako Nozawa, Koichi Yamadera, Masakazu Morita, Ryo Horikawa, Hiromi Tsuru', 'ji-paraná', 0.000005, 0.000006, '2025-12-07 16:09:43.260354 +00:00', '2025-12-07 16:09:43.260354 +00:00');
