INSERT INTO public.filmes (id, tmdb_id, titulo, titulo_original, sinopse, data_lancamento, genero, poster_path, lingua, duracao, nota_media, elenco_principal, cidade_referencia, latitude, longitude, data_criacao, data_atualizacao) VALUES (24, 100402, 'Captain America: The Winter Soldier', 'Captain America: The Winter Soldier', 'After the cataclysmic events in New York with The Avengers, Steve Rogers, aka Captain America is living quietly in Washington, D.C. and trying to adjust to the modern world. But when a S.H.I.E.L.D. colleague comes under attack, Steve becomes embroiled in a web of intrigue that threatens to put the world at risk. Joining forces with the Black Widow, Captain America struggles to expose the ever-widening conspiracy while fighting off professional assassins sent to silence him at every turn. When the full scope of the villainous plot is revealed, Captain America and the Black Widow enlist the help of a new ally, the Falcon. However, they soon find themselves up against an unexpected and formidable enemy—the Winter Soldier.', '2014-03-20', 'Action, Adventure, Science Fiction', '/tVFRpFw3xTedgPGqxW0AOI8Qhh0.jpg', 'en', 136, 76.0, 'Chris Evans, Samuel L. Jackson, Scarlett Johansson, Robert Redford, Sebastian Stan', null, null, null, '2025-12-07 16:33:49.679038 +00:00', '2025-12-07 16:33:49.679038 +00:00');
INSERT INTO public.filmes (id, tmdb_id, titulo, titulo_original, sinopse, data_lancamento, genero, poster_path, lingua, duracao, nota_media, elenco_principal, cidade_referencia, latitude, longitude, data_criacao, data_atualizacao) VALUES (25, 768744, 'My Hero Academia: World Heroes'' Mission', '僕のヒーローアカデミア THE MOVIE ワールド ヒーローズ ミッション', 'A mysterious group called Humarize strongly believes in the Quirk Singularity Doomsday theory which states that when quirks get mixed further in with future generations, that power will bring forth the end of humanity. In order to save everyone, the Pro-Heroes around the world ask UA Academy heroes-in-training to assist them and form a world-class selected hero team. It’s up to the heroes to save the world and the future of heroes in what is the most dangerous crisis to take place yet in My Hero Academia.', '2021-08-06', 'Animation, Action, Adventure, Science Fiction', '/AsTlA7dj2ySGY1pzGSD0MoHFhEF.jpg', 'ja', 105, 75.0, 'Daiki Yamashita, Nobuhiko Okamoto, Yuki Kaji, Ryo Yoshizawa, Kazuya Nakai', null, null, null, '2025-12-07 16:34:12.667740 +00:00', '2025-12-07 16:34:12.667740 +00:00');
INSERT INTO public.filmes (id, tmdb_id, titulo, titulo_original, sinopse, data_lancamento, genero, poster_path, lingua, duracao, nota_media, elenco_principal, cidade_referencia, latitude, longitude, data_criacao, data_atualizacao) VALUES (26, 317442, 'The Last: Naruto the Movie', 'THE LAST -NARUTO THE MOVIE-', 'Two years after the events of the Fourth Great Ninja War, the moon that Hagoromo Otsutsuki created long ago to seal away the Gedo Statue begins to descend towards the world, threatening to become a meteor that would destroy everything on impact. Amidst this crisis, a direct descendant of Kaguya Otsutsuki named Toneri Otsutsuki attempts to kidnap Hinata Hyuga but ends up abducting her younger sister Hanabi. Naruto and his allies now mount a rescue mission before finding themselves embroiled in a final battle to decide the fate of everything.', '2014-12-06', 'Action, Romance, Animation', '/bAQ8O5Uw6FedtlCbJTutenzPVKd.jpg', 'ja', 114, 99.9, 'Junko Takeuchi, Nana Mizuki, Jun Fukuyama, Chie Nakamura, Showtaro Morikubo', null, null, null, '2025-12-07 16:44:34.514753 +00:00', '2025-12-07 16:44:34.514800 +00:00');
